    },
    "plugins": ["jest"],
    "parserOptions": {
      "sourceType": "module"
    },
    "extends": ["@meese-os/eslint-config", "plugin:jest/recommended"]
  },
  "stylelint": {
    "extends": ["stylelint-config-standard-scss", "@meese-os/stylelint-config"]
  },
  "babel": {
    "presets": [
      "@babel/preset-env"
    ],
    "plugins": [
      "@babel/plugin-transform-runtime"
    ]
  },
  "esdoc": {
    "source": "./src",
    "destination": "./doc",
    "plugins": [
      {
        "name": "esdoc-standard-plugin",
        "option": {
          "brand": {
            "title": "meeseOS Core API",
            "description": "meeseOS Core API Documentation"
          },
          "lint": {
            "enable": false
          },
          "coverage": {
            "enable": false
          },
          "undocumentIdentifier": {
            "enable": false
          }
        }
      },
      {
        "name": "esdoc-publish-html-plugin"
      },
      {
        "name": "esdoc-ecmascript-proposal-plugin",
        "option": {
          "all": true
        }
      }
    ]
  },
  "jest": {
    "collectCoverage": true,
    "testEnvironment": "jsdom",
    "coverageReporters": [
      "lcov"
    ],
    "setupFilesAfterEnv": [
      "<rootDir>/__mocks__/setup.js"
    ],
    "moduleNameMapper": {
      "^meeseOS$": "<rootDir>/__mocks__/core.js",
      "^.+\\.html?$": "html-loader-jest",
      "^.+\\.scss$": "<rootDir>/__mocks__/null-module.js",
      "^.+\\.(png|jpe?g|gif)$": "<rootDir>/__mocks__/null-module.js"
    },
    "coveragePathIgnorePatterns": [
      "src/config.js",
      "src/providers",
      "/node_modules/"
    ]
  },
  "repository": {
    "type": "git",
    "url": "https://github.com/meeseOS/meeseOS.git",
    "directory": "frontend/client"
  },
  "keywords": [
    "os",
    "osjs",
    "operating system",
    "webOS",
    "meeseOS",
    "client"
  ]
}
